SELECT Disciplina_Area.Area,	   COUNT(Staff.DocentesID) AS qnt_docentes,	   CAST((AVG(Disciplina_Area.Custo) * 0.33) AS DECIMAL (5,3)) AS media_salarioFROM StaffFULL OUTER JOIN 	(SELECT Disciplinas.DisciplinaID,			Disciplinas.Nome AS Disciplina,			Disciplinas.Custo,			Area.Nome AS Area	 FROM Disciplinas	 FULL OUTER JOIN Area	 ON Disciplinas.Area = Area.AreaID) AS Disciplina_AreaON Staff.Disciplina = Disciplina_Area.DisciplinaIDGROUP BY Disciplina_Area.AreaORDER BY qnt_docentes DESC